"use strict";function Car(){}Car.prototype.getCar=function(){return JSON.parse(localStorage.getItem("cartlist"))||[]},Car.prototype.addToCar=function(t){var r=this.getCar();if(this.hasGoods(t.id))for(var e=0;e<r.length;e++)r[e].id==t.id&&(r[e].number+=1);else r.push(t);alert("加入成功"),localStorage.setItem("cartlist",JSON.stringify(r))},Car.prototype.hasGoods=function(t){for(var r=this.getCar(),e=0;e<r.length;e++)if(r[e].id==t)return!0;return!1},Car.prototype.getTotalPrice=function(){for(var t=0,r=this.getCar(),e=0;e<r.length;e++)t+=parseFloat(r[e].price)*parseInt(r[e].number);return t},Car.prototype.getTotalNumber=function(){for(var t=0,r=this.getCar(),e=0;e<r.length;e++)t+=parseInt(r[e].number);return t},Car.prototype.delGoods=function(t){for(var r=this.getCar(),e=0;e<r.length;e++)if(r[e].id==t)return r.splice(e,1),localStorage.setItem("cartlist",JSON.stringify(r)),!0;return!1};